package javaPackage;

public class EvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int a =50;a<=60;a++)
		{
			if(a%2 == 0)
			{
				System.out.println("This is a even number "+a);
			}
			else
				System.out.println("this is the odd number "+a);
		}

	}

}
